import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Logo from '../../assets/logo.png';
import './style.css';

function Navbar(){

    const handleDownloadBackup = () => {
        const downloadUrl = 'http://200.6.137.202:9093/download/exe';
      
        // Defina os dados a serem enviados no corpo da solicitação
        const data = {
          clientId: 'seuClientIdAqui'
        };
      
        fetch(downloadUrl, {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('sessionToken')}`
  },
          body: JSON.stringify(data)
        })
          .then(response => response.blob())
          .then(blob => {
            // Cria um link temporário para realizar o download
            const link = document.createElement('a');
            link.href = URL.createObjectURL(blob);
            link.setAttribute('download', 'BackupPlusInstall.exe'); // Defina o nome do arquivo aqui
            link.style.display = 'none';
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
          })
          .catch(error => {
            console.error('Erro ao realizar o download:', error);
          });
      };

    const navigate = useNavigate();
    const [busca, setBusca] = useState('');

    function Buscar(){
        navigate(`/busca?q=${busca}`);
    }

    function openSidebar(){
        const event = new CustomEvent('openSidebar');
        window.dispatchEvent(event);
    }

    function Logout(){
        localStorage.removeItem('sessionToken');
        localStorage.removeItem('sessionId');
        localStorage.removeItem('sessionEmail');
        localStorage.removeItem('sessionCodCidade');
        localStorage.removeItem('sessionCidade');
        localStorage.removeItem('sessionUF');
        navigate('/login');
    }

    return <nav className="navbar fixed-top navbar-expand-lg navbar-primary bg-primary ps-3 pe-3">
        <div className="container-fluid">
            {/*<Link className="navbar-brand" to="/"><img className="mt-1" src={Logo} /></Link>*/}
            
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
            </button>

            <div className="collapse navbar-collapse" id="navbarSupportedContent">

                <div className="ms-auto me-auto mt-1">
                   
                </div>

                <div className="mt-1">
                    <Link to="/clientes" className="btn btn-outline-light me-3">
                        Clientes
                    </Link>

                    <Link to="#" onClick={handleDownloadBackup} className="btn btn-outline-light me-3">
                        Download <i className="bi bi-download"></i>
                    </Link>
                    {
                    //<button className="btn btn-outline-danger me-3"><i className="fas fa-sign-in-alt"></i> Acessar</button>
                    }

                    <div className="btn-group">     
                        <button type="button" className="btn btn-outline-light me-3 dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                            <i className="fas fa-user"></i>
                        </button>
                        <ul className="dropdown-menu">
                            <li><Link to="/perfil" className="dropdown-item">Perfil</Link></li>
                            <li><hr className="dropdown-divider" /></li>
                            <li><a href="#" onClick={Logout} className="dropdown-item">Sair</a></li>
                        </ul>
                    </div>

                </div>

            </div>
        </div>
    </nav>
}

export default Navbar;
