import Navbar from "../../components/navbar/index.jsx";
import Footer from "../../components/footer/index.jsx";
import { Link, useNavigate, useParams } from "react-router-dom";
import { IMaskInput } from 'react-imask'; 
import {CBackup} from "../../components/backup/backup.jsx";

import { useState } from "react";
import { useEffect } from "react";
import api from "../../services/api.js";


function Backup(){
    
    //const {cod_comandantes, setCodComandantes} = useParams();
    const navigate = useNavigate();

    const [IdCliente, setIDCliente] = useState("");
    const [IdDispositivo, setIDDispositivo] = useState("");
    const [IdBackup, setIDBackup] = useState("");
    const [descricao, setDescricao] = useState("");
    const [caminho, setCaminho] = useState("");
    const [segunda, setSegunda] = useState("");
    const [terca, setTerca] = useState("");
    const [quarta, setQuarta] = useState("");
    const [quinta, setQuinta] = useState("");
    const [sexta, setSexta] = useState("");
    const [sabado, setSabado] = useState("");
    const [domingo, setDomingo] = useState("");
    const [hora, setHora] = useState("");
    const [sub_pasta, setSubPasta] = useState("");
    
    const [msg, setMsg] = useState("");

    const [backup, setBackup] = useState([]);  

    
    function handleChange(e) {
        setSegunda(e.target.checked ? 'S' : 'N');
        setTerca(e.target.checked ? 'S' : 'N');
        setQuarta(e.target.checked ? 'S' : 'N');
        setQ(e.target.checked ? 'S' : 'N');
        setSegunda(e.target.checked ? 'S' : 'N');
        setSegunda(e.target.checked ? 'S' : 'N');
        setSegunda(e.target.checked ? 'S' : 'N');
        setSegunda(e.target.checked ? 'S' : 'N');
        setSegunda(e.target.checked ? 'S' : 'N');
        setSegunda(e.target.checked ? 'S' : 'N');
      }
    
   
    function Limpar(){
        setIDCliente("");
        setIDDispositivo("");
        setIDBackup("");
        setDescricao("");
        setCaminho("");
        setSegunda("");
        setTerca("");
        setQuarta("");
        setQuinta("");
        setSexta("");
        setSabado("");
        setDomingo("");
        setHora("");
        setSubPasta("");
    }

    function consultaBackup(){
        api.get('/clientes/'+IdCliente+'/dispositivos/'+IdDispositivo+'/backup')
        .then((retorno) => {
            setBackup(retorno.data)
            console.log(retorno.data);

        })
        .catch((err) => {
            setBackup([]);

            if (err.response) {
                console.log(err.response.data);
            }
        });   
    } 
    
    function ConsultaBackupID(){
        api.get('/clientes/'+IdCliente+'/dispositivos/'+IdDispositivo+'/backup/'+IdBackup)
        .then((retorno) => {
            setBackup(retorno.data)
            console.log(retorno.data);

        })
        .catch((err) => {
            setBackup([]);

            if (err.response) {
                console.log(err.response.data);
            }
        });   
    } 

    /*function ativarCliente(){
        api.put(`/clientes/ativar/`+IdCliente)
        .then((retorno) => {
            consultaClientesInativos()
            console.log(IdCliente);

        })
        .catch((err) => {
            setClientes([]);

            if (err.response) {
                console.log(err.response.data);
                
            }
        });   
    }*/  

    function Excluir(){
        api.delete('/clientes/'+IdCliente+'/dispositivos/'+IdDispositivo+'/backup/'+IdBackup)
        .then((retorno) => {
            setBackup()
            console.log(IdDispositivo);
        })
        .catch((err) => {
            setBackup([]);
            if (err.response) {
                console.log(err.response.data);
            }
        });   
    }  

    /*function consultaClientesInativos(){
        api.get('/clientes/inativos')
        .then((retorno) => {
            setClientes(retorno.data)
            console.log(retorno.data);

        })
        .catch((err) => {
            setClientes([]);

            if (err.response) {
                console.log(err.response.data);
            }
        });    
    }*/ 

    function SalvarDados() {
        const dados = {
            cod_dispositivo : IdDispositivo ,
            cod_backup : IdBackup,
            descricao,
            caminho,
            segunda,
            terca,
            quarta,
            quinta,
            sexta,
            sabado,
            domingo,
            hora,
            sub_pasta
        };
      
        if (IdBackup > 0) {
          // Editar cliente existente
          api
            .put('/clientes/'+ IdCliente+'/dispositivos/'+ IdDispositivo +'/editar/'+IdBackup , dados)
            .then((retorno) => {
                consultaBackup();
              console.log("Editado com sucesso!");
              Limpar();
            })
            .catch((err) => {
              setBackup([]);
      
              if (err.response) {
                console.log(err.response.data);
              }
            });
        } else {
          // Cadastrar novo backup
          api
            .post('/clientes/'+ IdCliente+'/dispositivos/'+ IdDispositivo +'/cadastro', dados)
            .then((retorno) => {
                consultaBackup();
              console.log("Cadastrado com sucesso!");
              Limpar();
            })
            .catch((err) => {
              setBackup([]);
      
              if (err.response) {
                console.log(err.response.data);
              }
            });
        }
      }

    useEffect(() => {
       // consultaBackups();
    }, []);

    return <>
    <Navbar />

    <div  /* MODAL PARA CADASTRAR/EDITAR BACKUPS */ className="modal fade" id="Cadastrar" data-bs-backdrop="static" data-bs-keyboard="false" x     aria-labelledby="CadastrarComandanteLabel" aria-hidden="true">
        <div className="modal-dialog modal-dialog-centered modal-lg ">
            <div className="modal-content">
                <div className="modal-header">                   
                    <h5 className="modal-title" id="SelecionarLabe2">Backup  {': '+IdBackup} {'-'+descricao}</h5>
                    <button type="button" onClick={Limpar} className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>

                <div className="modal-body">
                    <p></p>
                    <div className="row">
                        <div className="col-sm-12 col-md-6">
                            <div className="mb-3">
                                <label>DescriÃ§Ã£o</label>
                                <input type="text" onChange={(e) => setDescricao(e.target.value)} value={descricao} className="form-control" id="modelNome" placeholder=""></input>
                            </div>                                                                              
                        </div> 
                        
                        <div className="col-sm-12 col-md-6">
                            <div className="mb-3">
                                <label>Caminho</label>
                                <input type="text" onChange={(e) => setCaminho(e.target.value)} value={caminho} className="form-control" id="modelNome" placeholder=""></input>
                            </div>                                                                              
                        </div> 
                        <form>
                            <div className="row">
                                <div className="form-group col-sm-4 col-md-3 col-lg-3">
                                    <input type="checkbox" onClick={(e) => setSegunda('S')} value={caminho} 
                                        className="form-check-input" id="CheckSegunda" placeholder=""></input>
                                    <label className="ms-1 form-check-label" for="CheckSegunda">Segunda</label>
                                </div> 

                                <div className="form-group col-sm-4 col-md-3 col-lg-3">
                                    <input type="checkbox" onChange={(e) => setTerca(e.target.value)} value={caminho} 
                                        className="form-check-input" id="CheckTerca" placeholder=""></input>
                                    <label className="ms-1 form-check-label" for="CheckTerca">TerÃ§a</label>
                                </div>

                                <div className="form-group col-sm-4 col-md-3 col-lg-3">
                                    <input type="checkbox" onChange={(e) => setQuarta(e.target.value)} value={caminho} 
                                        className="form-check-input" id="CheckQuarta" placeholder=""></input>
                                    <label className="ms-1 form-check-label" for="CheckQuarta">Quarta</label>
                                </div>

                                <div className="form-group col-sm-4 col-md-3 col-lg-3">
                                    <input type="checkbox" onChange={(e) => setQuinta(e.target.value)} value={caminho} 
                                        className="form-check-input" id="CheckQuinta" placeholder=""></input>
                                    <label className="ms-1 form-check-label" for="CheckQuinta">Quinta</label>
                                </div>
                                  
                                <div className="form-group col-sm-4 col-md-3 col-lg-3">
                                    <input type="checkbox" onChange={(e) => setSexta(e.target.value)} value={caminho} 
                                        className="form-check-input" id="CheckSexta" placeholder=""></input>
                                    <label className="ms-1 form-check-label" for="CheckSexta">Sexta</label>
                                </div>
                                 

                                <div className="form-group col-sm-4 col-md-3 col-lg-3">
                                    <input type="checkbox" onChange={(e) => setSabado(e.target.value)} value={caminho} 
                                        className="form-check-input" id="CheckSabado" placeholder=""></input>
                                    <label className="ms-1 form-check-label" for="CheckSabado">Sabado</label>
                                </div>
                                 

                                <div className="form-group col-sm-4 col-md-3 col-lg-3">
                                    <input type="checkbox" onChange={(e) => setDomingo(e.target.value)} value={caminho} 
                                        className="form-check-input" id="CheckDomingo" placeholder=""></input>
                                    <label className="ms-1 form-check-label" for="CheckDomingo">Domingo</label>
                                </div>
                                 
                                <div className="divider col-sm-12"></div>
                                <div className="form-group mt-3 col-sm-12">
                                    <input type="checkbox" onChange={(e) => setSubPasta(e.target.value)} value={caminho} 
                                        className="form-check-input" id="CheckSubPasta" placeholder=""></input>
                                    <label className="ms-1 form-check-label" for="CheckSubPasta">Enviar SubPastas</label>
                                </div>
                                 
                            </div>
                        </form>

                        <div className="mt-3 col-sm-4 col-md-4">
                            <div className="mb-3">
                                <label>Hora do Backup</label>
                                <input type="time" onChange={(e) => setHora(e.target.value)} value={hora} className="form-control" id="modelNome" placeholder=""></input>
                            </div>                                                                              
                        </div>

                    </div>                                         
                </div>    

                <div className="modal-footer">
                    <button type="button" onClick={SalvarDados} data-bs-dismiss="modal" aria-label="Close" className="btn btn-primary">Salvar</button>
                </div>
            </div>
        </div>
    </div>
    <div  className="mt-5 container-fluid p-5">

        <h2>Backup</h2>
        
        <div /*ABAS*/ className="mt-2 tab-content" id="conteudoMinhaAba">

            <div  /* MODAL PARA EXCLUIR */ className="modal fade" id="ConfirmaExcluirBackup" data-bs-backdrop="static" 
                        data-bs-keyboard="false" aria-labelledby="ConfirmaExcluirBackupLabel" aria-hidden="true">
                    <div className="modal-dialog modal-dialog-centered modal-md ">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="ConfirmaExcluirBackupLabel">
                                    Esse procedimento Ã© irreversivel e todo o backup serÃ¡ perdido.</h5>
                                <h5 className="modal-title" id="ConfirmaExcluirBackupLabel">Confirma a ExclusÃ£o do Backup?</h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    data-bs-dismiss="modal"
                                    aria-label="Close"
                                    onClick={() => {
                                        setIDBackup
                                        (null);
                                    }}>
                                </button>
                            </div>
                            
                            <div className="modal-footer">
                                <button type="button" onClick={Excluir} data-bs-dismiss="modal" aria-label="Close" className="btn btn-primary">Confirmar</button>
                                <button type="button" data-bs-dismiss="modal" aria-label="Close" className="btn btn-danger">Cancelar</button>
                            </div>  
                        </div>
                    </div>
                </div>
            <div /*ATIVOS*/ className="tab-pane fade show active" id="Ativos" role="tabpanel" aria-labelledby="aba-Ativos">
                <div className="mt-2">  
                    <button /*BOTÃƒO CHAMADA DO MODAL DE CADASTRAR*/ type="button" className="btn mb-2 btn-success" onClick={() => {
                                           Limpar();
                                          }} data-bs-toggle="modal" data-bs-target="#Cadastrar">
                        Cadastrar Backup
                    </button>
                    <div /*TABELA DE VIEW*/ className="table-responsive"> 

                        {
                            backup.map((p_backup) =>{
                                return <CBackup key ={p_backup.cod_backup}
                                    codigo = {p_backup.cod_backup}
                                    descricao = {p_backup.descricao}
                                    hora = {p_backup.hora}
                                    dt_cadastro = {p_backup.dt_cadastro}
                                    dt_realizado = {p_backup.dt_realizado}
                                />
                            })
                        }
                    </div>
                </div>
            </div>
        </div>
    </div>

    <Footer />
</>
}

export default Backup;
