import Navbar from "../../components/navbar/index.jsx";
import Footer from "../../components/footer/index.jsx";
//import { Link, useNavigate, useParams } from "react-router-dom";
import { IMaskInput } from 'react-imask'; 
import {CClientes, CClientes_inativos} from "../../components/clientes/clientes.jsx";

import { useState } from "react";
import { useEffect } from "react";
import api from "../../services/api.js";


function Clientes(){
    
    //const {cod_comandantes, setCodComandantes} = useParams();
    //const navigate = useNavigate();

    const [IdCliente, setIDCliente] = useState("");
    const [nome, setNome] = useState("");
    const [cpf, setCpf] = useState("");
    const [email, setEmail] = useState("");
    const [gb, setGB] = useState("");
    const [senha, setSenha] = useState("");
    const [confirmar_senha, setConfirmarSenha] = useState("");
    const [cod_usuario, setCod_usuario] = useState("1");
    const [ativo, setAtivo] = useState("");
    
    const [msg, setMsg] = useState("");

    const [clientes, setClientes] = useState([]);  
    const [clientesInativos, setClientesInativos] = useState([]);  
    
    
    const [IDComandanteSelecionado, setIDComandanteSelecionado] = useState(null);
    const [NomeComandanteSelecionado, setNomeComandanteSelecionado] = useState(null); 
   
    function Limpar(){
        setIDCliente("");
        setNome("");
        setCpf("");
        setGB("");
        setSenha("");
        setConfirmarSenha("");
        setAtivo("");
    }

    function ValidaSenha(){
        if (senha !== co) {
            alert('As senhas não coincidem. Por favor, digite novamente.');
            return;
          }


    }

    function consultaClientes(){
        api.get('/clientes/S',  { headers: { 'usuario': cod_usuario } })
      
        .then((retorno) => {
            setClientes(retorno.data)
            console.log(retorno.data);

        })
        .catch((err) => {
            setClientes([]);

            if (err.response) {
                console.log(err.response.data);
            }
        });   
    } 
    
    function ConsultaClienteID(){
        api.get('/clientes/'+IdCliente)
        .then((retorno) => {
            setClientes(retorno.data)
            console.log(retorno.data);

        })
        .catch((err) => {
            setClientes([]);

            if (err.response) {
                console.log(err.response.data);
            }
        });   
    } 

    function ativarCliente(){
        api.put(`/clientes/ativar/`+IdCliente)
        .then((retorno) => {
            consultaClientesInativos()
            console.log(IdCliente);

        })
        .catch((err) => {
            setClientes([]);

            if (err.response) {
                console.log(err.response.data);
                
            }
        });   
    }  

    function Excluir(){
        api.put(`/clientes/excluir/`+IdCliente)
        .then((retorno) => {
            consultaClientes()
            console.log(IDComandanteSelecionado);
        })
        .catch((err) => {
            setClientes([]);
            if (err.response) {
                console.log(err.response.data);
            }
        });   
    }  

    function consultaClientesInativos(){
        api.get('/clientes/N', { headers: { 'usuario': cod_usuario } })
        .then((retorno) => {
            setClientesInativos(retorno.data)
            console.log(retorno.data);

        })
        .catch((err) => {
            setClientesInativos([]);

            if (err.response) {
                console.log(err.response.data);
            }
        });
            
    } 

    function SalvarDados() {
        const dados = {
          cpf,
          nome,
          email,
          senha,
          gb,
          ativo
        };
      
        if (IdCliente > 0) {
          // Editar cliente existente
          api
            .put(`/clientes/editar/` + IdCliente, dados, { headers: { 'usuario': cod_usuario } })
            .then((retorno) => {
                consultaClientes();
              console.log("Editado com sucesso!");
              Limpar();
            })
            .catch((err) => {
              setClientes([]);
      
              if (err.response) {
                console.log(err.response.data);
              }
            });
        } else {
          // Cadastrar novo cliente
          api
            .post(`/clientes/cadastro`, dados, { headers: { 'usuario': cod_usuario } })
            .then((retorno) => {
                consultaClientes();
              console.log("Cadastrado com sucesso!");
              Limpar();
            })
            .catch((err) => {
              setClientes([]);
      
              if (err.response) {
                console.log(err.response.data);
              }
            });
        }
      }

    useEffect(() => {
        consultaClientes();
    }, []);

    return <>
    <Navbar />

    <div  /* MODAL PARA CADASTRAR/EDITAR CLIENTES */ className="modal fade" id="Cadastrar" data-bs-backdrop="static" data-bs-keyboard="false" x     aria-labelledby="CadastrarComandanteLabel" aria-hidden="true">
        <div className="modal-dialog modal-dialog-centered modal-md ">
            <div className="modal-content">
                <div className="modal-header">
                    <h5 className="modal-title" id="SelecionarLabe2">Clientes</h5>
                    <button type="button" onClick={Limpar} className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>

                <div className="modal-body">
                    <p></p>        
                    <div>
                        <div className="mb-3">
                            <label>CPF/CNPJ</label>
                            <input type="number" as={IMaskInput} mask="000.000.000-00" value={cpf} onChange={(e) => setCpf(e.target.value)} 
                            className="form-control" id="modelCPF" placeholder=""></input>
                        </div>                                                                              
                    </div>   
                    <div>
                        <div className="mb-3">
                            <label>Nome</label>
                            <input type="text" onChange={(e) => setNome(e.target.value)} value={nome} className="form-control" id="modelNome" placeholder=""></input>
                        </div>                                                                              
                    </div> 
                    <div>
                        <div className="mb-3">
                            <label>Email</label>
                            <input type="email" onChange={(e) => setEmail(e.target.value)} value={email} className="form-control" id="modelNome" placeholder=""></input>
                        </div>                                                                              
                    </div> 
                    <div>
                        <div className="mb-3">
                            <label>Gb</label>
                            <input type="number" onChange={(e) => setGB(e.target.value)} value={gb} className="form-control" id="modelNome" placeholder=""></input>
                        </div>                                                                              
                    </div> 
                    <div>
                        <div className="mb-3">
                            <label>Senha</label>
                            <input type="password" onChange={(e) => setSenha(e.target.value)} value={senha} className="form-control" id="modelNome" placeholder=""></input>
                        </div>    
                        <div className="mb-3">
                            <label>Confirmar Senha</label>
                            <input type="password" onChange={(e) => setConfirmarSenha(e.target.value)} value={confirmar_senha} className="form-control" id="modelNome" placeholder=""></input>
                        </div>                                                                             
                    </div>    

                                            
                </div>    

                <div className="modal-footer">
                    <button type="submit" onClick={ValidaSenha} data-bs-dismiss="" aria-label="" className="btn btn-primary">Salvar</button>
                </div>
            </div>
        </div>
    </div>
    <div  className="mt-5 container-fluid p-5">

        <h2>Clientes</h2>
        <ul /*ABAS DE NAVEGAÇÃO*/ className="nav nav-pills nav-fill" id="minhaAba" role="tablist">
            <li className="nav-item" role="presentation">
                <button className="nav-link active rounded-bottom-0" id="aba-Ativos" data-bs-toggle="tab" data-bs-target="#Ativos" type="button"
                    role="tab" aria-controls="inicial" onClick={consultaClientes} aria-selected="true">Ativos</button>
            </li>
            <li className="nav-item" role="presentation">
                <button className="nav-link rounded-bottom-0" id="aba-Inativos" data-bs-toggle="tab" data-bs-target="#Inativos" type="button"
                    role="tab" aria-controls="pefil" aria-selected="false" onClick={consultaClientesInativos}>Inativos</button>
            </li>
        </ul>
        <div className="mb-2"><div className="border border-primary"></div></div>
        <div /*ABAS*/ className="tab-content" id="conteudoMinhaAba">

            <div  /* MODAL PARA EXCLUIR */ className="modal fade" id="ConfirmaExcluirCliente" data-bs-backdrop="static" 
                        data-bs-keyboard="false" aria-labelledby="ConfirmaExcluirClienteLabel" aria-hidden="true">
                    <div className="modal-dialog modal-dialog-centered modal-md ">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="ConfirmaExcluirClienteLabel">Confirma a Exclusão do Comandante?</h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    data-bs-dismiss="modal"
                                    aria-label="Close"
                                    onClick={() => {
                                        setIDCliente
                                        (null);
                                    }}>
                                </button>
                            </div>
                            
                            <div className="modal-footer">
                                <button type="button" onClick={Excluir} data-bs-dismiss="modal" aria-label="Close" className="btn btn-primary">Confirmar</button>
                                <button type="button" data-bs-dismiss="modal" aria-label="Close" className="btn btn-danger">Cancelar</button>
                            </div>  
                        </div>
                    </div>
                </div>
            <div /*ATIVOS*/ className="tab-pane fade show active" id="Ativos" role="tabpanel" aria-labelledby="aba-Ativos">
                <div className="mt-2">  
                    <button /*BOTÃO CHAMADA DO MODAL DE CADASTRAR*/ type="button" className="btn mb-2 btn-success" onClick={() => {
                                            setIDCliente("");
                                            setNome(""); setCpf(""); setEmail(""); setSenha(""); setConfirmarSenha(""); setGB("")
                                          }} data-bs-toggle="modal" data-bs-target="#Cadastrar">
                        Cadastrar Cliente
                    </button>
                    <div /*TABELA DE VIEW*/ className="table-responsive"> 

                        <table className="table table-hover table-striped">
                            <caption>Lista de Clientes Ativos</caption>
                            <thead>
                                <tr className="align-middle">
                                    <th scope="col">Codigo</th>
                                    <th scope="col">Nome</th>
                                    <th scope="col">Usado</th>
                                    <th scope="col">Disponivel</th>
                                    <th scope="col">Data de Cadastro</th>
                                    <th className="text-center" scope="col">Ações</th>
                                </tr>
                                          
                            </thead>
                            <tbody>
                            {
                                    clientes.map((p_clientes) => {
                                    return <CClientes key={p_clientes.cod_cliente}
                                        codigo={p_clientes.cod_cliente}
                                        nome={p_clientes.descricao}
                                        usado={p_clientes.gb}
                                        disponivel={p_clientes.gb}
                                        dt_cadastro={p_clientes.dt_cadastro}
                                        
                                        editar={<button type="button"  onClick={() => {
                                            setIDCliente(p_clientes.cod_cliente);
                                            setNome(p_clientes.descricao); setCpf(p_clientes.cpf)
                                            setEmail(p_clientes.email); setGB(p_clientes.gb)
                                          }} data-bs-toggle="modal" data-bs-target="#Cadastrar"  
                                                 className="btn btn-sm btn-primary m-1 mt-1">Editar</button>}
                                        
                                        
                                        
                                        excluir={<button
                                            type="button"
                                            onClick={() => {
                                              setIDCliente(p_clientes.cod_cliente);
                                            }}
                                            data-bs-toggle="modal" 
                                            data-bs-target="#ConfirmaExcluirCliente" 
                                            className="btn btn-sm btn-danger m-0 mt-0"
                                          >
                                           Excluir
                                          </button>}
                                    />
                                    })
                                }       
                            </tbody>
                            
                        </table> 

                    </div>
                </div>
            </div>

            <div  /* MODAL PARA ATIVAR */ className="modal fade" id="ConfirmaAtivaCliente" data-bs-backdrop="static" 
                    data-bs-keyboard="false" aria-labelledby="ConfirmaAtivaComandanteLabel" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered modal-md ">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="ConfirmaAtivaClienteLabel">Confirma a ativação do Cliente?</h5>
                            <button
                                type="button"
                                className="btn-close"
                                data-bs-dismiss="modal"
                                aria-label="Close"
                                onClick={() => {
                                    setIDCliente(null);
                                }}>
                            </button>
                        </div>
                        
                        <div className="modal-footer">
                            <button type="button" onClick={ativarCliente} data-bs-dismiss="modal" aria-label="Close" className="btn btn-primary">Confirmar</button>
                            <button type="button" data-bs-dismiss="modal" aria-label="Close" className="btn btn-danger">Cancelar</button>
                        </div>  
                    </div>
                </div>
            </div>

            <div  /*INATIVOS*/ className="tab-pane fade" id="Inativos" role="tabpanel" aria-labelledby="aba-Inativos">
                <div className="mt-2">  
                    <div /*TABELA DE VIEW*/ className="table-responsive"> 

                        <table className="table table-hover table-striped">
                            <caption>Lista de Clientes Inativos</caption>
                            <thead>
                                <tr className="align-middle">
                                    <th scope="col">Codigo</th>
                                    <th scope="col">Nome</th>
                                    <th className="text-center" scope="col">Data de Cadastro</th>
                                    <th className="text-center" scope="col">Ativar</th>
                                </tr>
                            </thead>
                            <tbody>
                            {
                                    clientesInativos.map((p_clientesInativos) => {
                                    return <CClientes_inativos key={p_clientesInativos.cod_cliente}
                                        codigo={p_clientesInativos.cod_cliente}
                                        nome={p_clientesInativos.descricao}
                                        dt_cadastro={p_clientesInativos.dt_cadastro}
                                        ativar={<button
                                            type="button"
                                            onClick={() => {
                                              setIDCliente(p_clientesInativos.cod_cliente);
                                            }}
                                            data-bs-toggle="modal" 
                                            data-bs-target="#ConfirmaAtivaCliente" 
                                            className="btn btn-success m-0 mt-0"
                                          >
                                            Ativar
                                          </button>}
                                    />
                                    })
                                }       
                            
                            </tbody>
                            
                        </table> 

                    </div>
                </div>
            </div>
        </div>
    </div>

    <Footer />
</>
}

export default Clientes;
