function CArquivos(props){

    const dt_cadastro = new Date(props.dt_cadastro);
    const dt_realizado = new Date(props.dt_realizado);
    const tamanho = (props.tamanho / 1024).toFixed(2)
    //- Data realizado: {new Intl.DateTimeFormat('pt-BR', {dateStyle: 'short'}).format(dt_realizado)}
    return<>    
        <div class="card">
            <div class="card-body">
                <div className="row">
                    <div className="col-sm-12 col-md-6">
                        {props.descricao} -  
                        Tamanho:{tamanho > 999999.99
                        ? `${(tamanho / 1024 / 1024).toFixed(2)} GB`
                        : `${(tamanho / 1024).toFixed(2)} MB`}
                    </div>
                    <div className="d-flex justify-content-end col-sm-12 col-md-6">
                        <div>{props.btnBaixar}</div>{props.btnExcluir}
                    </div>
                </div>
                
            </div>
        </div>
    </>   
}


export {CArquivos };
