import imgArq from "../../assets/pasta.png";
import './backup.css';

function CBackup(props) {
    const dt_cadastro = new Date(props.dt_cadastro);
    const dt_realizado = new Date(props.dt_realizado);
    const tamanhoFixo = (props.tamanho / 1024).toFixed(2)
    const cod_backup = (props.codigo)
    const cod_cliente = (props.cod_cliente)
    const cod_dispositivo = (props.cod_dispositivo)

    const link = `/backup/${cod_backup}`
   
  
    return (
      <div className="col-sm-12 col-md-6 col-lg-4 col-xl-3">
        <a className="btn" href={link} onClick={() => {
          localStorage.setItem('sessionCod_Backup', cod_backup);
          localStorage.setItem('sessionCod_Cliente', cod_cliente);
          localStorage.setItem('sessionCod_Dispositivo', cod_dispositivo);
        }}>
          <div className="card text-center " to="/backup/:idbackup" style={{ width: "16rem" }}>
            <img className="card-img-top card-img-center" src={imgArq} alt="Imagem de capa do card" />
            <div className="card-body">
              <h5 className="text-center card-title"> {cod_backup} - {props.descricao}</h5>
              <p className="text-center card-text">
                {new Intl.DateTimeFormat('pt-BR', { dateStyle: 'short' }).format(dt_realizado)}-
                {props.Hr_realizado}
              </p>
              <p className="text-center card-text">
                {tamanhoFixo > 999999.99
                  ? `${(tamanhoFixo / 1024 / 1024).toFixed(2)} GB`
                  : `${(tamanhoFixo / 1024).toFixed(2)} MB`}
              </p>
              <div className="button-container">
                <button className="btn ms btn-primary btn-sm btn-block">Editar</button>
                <button className="btn btn-primary btn-sm btn-block">Excluir</button>
                {/* Adicione mais botões aqui, se necessário */}
              </div>
            </div>
          </div>
        </a>
      </div>
    );
    
  }
  
  export { CBackup };
