function CArquivos(props){

    const dt_cadastro = new Date(props.dt_cadastro);
    const dt_realizado = new Date(props.dt_realizado);
    const tamanho = (props.tamanho / 1024).toFixed(2)
    const cliente = (props.cod_cliente);

    //- Data realizado: {new Intl.DateTimeFormat('pt-BR', {dateStyle: 'short'}).format(dt_realizado)}
    return<>    
        <div class="card mb-1 ">
            <div class="card-body">
                <div className="row">
                    <div className="col-sm-12 col-md-10">
                        {props.descricao} - 
                        Tamanho: {tamanho > 999999.99
                        ? `${(tamanho / 1024 / 1024).toFixed(2)} GB`
                        : `${(tamanho / 1024).toFixed(2)} MB`}
                    </div>
                    <div className="d-flex justify-content-lg-end justify-content-sm-center justify-content-md-center col-sm-12 col-md-2">
                        <div className="me-1">{props.btnBaixar}</div> <div>{props.btnExcluir}</div>
                    </div>
                </div>
                
            </div>
        </div>
    </>   
}


export {CArquivos };
