function CDispositivos(props){

    const dt_cadastro = new Date(props.dt_cadastro);
    const tamanhoUsado = (props.usado / 1024).toFixed(2)
    const tamanhoAlocado = (props.disponivel * 1024).toFixed(2)
    const tamanhoDisponivel = (tamanhoAlocado * 1024) - tamanhoUsado

    return <tr className="align-middle">
        <td>{props.codigo}</td>
        <td>{props.descricao}</td>
        <td>{tamanhoUsado > 999999.99
                    ? `${(tamanhoUsado / 1024 / 1024).toFixed(2)} GB`
                    : `${(tamanhoUsado / 1024).toFixed(2)} MB`}
        </td>
        <td>{tamanhoDisponivel}</td>
        <td>{new Intl.DateTimeFormat('pt-BR', {dateStyle: 'short',timeStyle: 'short'}).format(dt_cadastro)}</td>
        <td className="text-center">
            {props.configurar}
            {props.editar}
            {props.excluir}
        </td>
    </tr>
}




export {CDispositivos };
