import imgArq from "../../assets/pasta.png";

function CBackup(props) {
    const dt_cadastro = new Date(props.dt_cadastro);
    const dt_realizado = new Date(props.dt_realizado);
    const Hr_realizado = new Date(props.Hr_realizado);
  
    return (
      <div className="col-sm-6 col-md-3 col-lg-2">
        <div className="card text-center" style={{ width: "16rem" }}>
          <img className="card-img-top card-img-center" src={imgArq} alt="Imagem de capa do card" />
          <div className="card-body">
            <h5 className="text-center card-title">ID: {props.codigo} - {props.descricao}</h5>
            <p className="text-center card-text">
                {new Intl.DateTimeFormat('pt-BR', {dateStyle: 'short'}).format(dt_realizado)} +
                {new Intl.DateTimeFormat('pt-BR', {timeStyle: 'short'}).format(Hr_realizado)}
            </p>
            <button href="/backup/:idbackup" className="btn btn-primary btn-sm btn-block">Visualizar</button>
          </div>
        </div>
      </div>
    );
  }
  
  export { CBackup };
