import React, { useState, useEffect } from "react";
import Navbar from "../../components/navbar/index.jsx";
import Footer from "../../components/footer/index.jsx";
import { Link, useNavigate, useParams } from "react-router-dom";
import { CArquivos } from "../../components/arquivos/arquivos.jsx";
import api from "../../services/api.js";

function Arquivos() {
    const navigate = useNavigate();

    const [IdCliente, setIDCliente] = useState("");
    const [IdDispositivo, setIDDispositivo] = useState("");
    const [IdBackup, setIDBackup] = useState("");
    const [descricao, setDescricao] = useState("");
    const [caminho, setCaminho] = useState("");
    const [dt_backup, setDt_backup] = useState("");
    const [dt_inicio, setDt_inicio] = useState("");
    const [dt_fim, setDt_fim] = useState("");
    const [ano, seAno] = useState("2023");
    const [mes, setMes] = useState("06");
    const [msg, setMsg] = useState("");
    const [backup, setBackup] = useState([]);


    const handleDownloadBackup = () => {
        const downloadUrl = 'http://localhost:4632/download';
        a
        const link = document.createElement('a');
        link.href = downloadUrl;
        link.setAttribute('download', 'nome-do-arquivo.ext'); // Defina o nome do arquivo aqui
        link.style.display = 'none';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      }

    function Limpar() {
        setDescricao("");
        setDt_backup("");
        setDt_inicio("");
        setDt_fim("");
    }

    function ConsultaArquivos() {
        
        api.get('/arquivos', { headers: {cliente: localStorage.getItem("sessionCod_Cliente"), ano:ano, mes:mes,
            dispositivo: localStorage.getItem("sessionCod_Dispositivo"),
            backup: localStorage.getItem("sessionCod_Backup")} })
            
            .then((retorno) => {
                setBackup(retorno.data.arquivos);
                console.log(retorno.data.arquivos);
            })
            .catch((err) => {
                setBackup([]);
                if (err.response) {
                    console.log(err.response.data.arquivos);
                }
            });
    }

    function Excluir() {
        api
            .delete(`/clientes/${IdCliente}/dispositivos/${IdDispositivo}/backup/${IdBackup}`)
            .then((retorno) => {
                ConsultaArquivos();
                console.log(IdDispositivo);
            })
            .catch((err) => {
                setBackup([]);
                if (err.response) {
                    console.log(err.response.data);
                }
            });
    }

    function SalvarDados() {
        const dados = {};

        if (IdBackup > 0) {
            // Editar cliente existente
            api
                .put(`/clientes/${IdCliente}/dispositivos/${IdDispositivo}/editar/${IdBackup}`, dados)
                .then((retorno) => {
                    ConsultaArquivos();
                    console.log("Editado com sucesso!");
                    Limpar();
                })
                .catch((err) => {
                    setBackup([]);
                    if (err.response) {
                        console.log(err.response.data);
                    }
                });
        } else {
            // Cadastrar novo backup
            api
                .post(`/clientes/${IdCliente}/dispositivos/${IdDispositivo}/cadastro`, dados)
                .then((retorno) => {
                    ConsultaArquivos();
                    console.log("Cadastrado com sucesso!");
                    Limpar();
                })
                .catch((err) => {
                    setBackup([]);
                    if (err.response) {
                        console.log(err.response.data);
                    }
                });
        }
    }

    useEffect(() => {
        ConsultaArquivos();
    }, []);

    return (
        <>
            <Navbar />
            <div className="mt-5 container-fluid p-5">
                <div className="d-flex justify-content-between">
                    <h2>Uploads</h2>

                    <div className="row">
                        <div className="col-md-4">
                            <div className="d-flex justify-content-between">
                                <label className="mt-1 me-1">Inicio</label>
                                <input type="date" className="form-control form-control-sm align-middle" id="DataInicio" placeholder="" />
                            </div>
                        </div>

                        <div className="col-md-4">
                            <div className="d-flex justify-content-between">
                                <label className="mt-1 me-1">Fim</label>
                                <input type="date" className="form-control form-control-sm align-middle" id="DataFim" placeholder="" />
                            </div>
                        </div>

                        <div className="col-md-4">
                            <button
                                type="button"
                                className="btn btn-primary btn-sm"
                                onClick={() => {
                                    Limpar();
                                }}
                                data-bs-toggle="modal"
                                data-bs-target="#Cadastrar"
                            >
                                Filtrar
                            </button>
                        </div>
                    </div>
                </div>

                <div className="mt-2 tab-content" id="conteudoMinhaAba">
                    <div className="modal fade" id="ConfirmaExcluirBackup" data-bs-backdrop="static" data-bs-keyboard="false" aria-labelledby="ConfirmaExcluirBackupLabel" aria-hidden="true">
                        <div className="modal-dialog modal-dialog-centered modal-md">
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h5 className="modal-title" id="ConfirmaExcluirBackupLabel">
                                        Esse procedimento é irreversível e todo o backup será perdido.
                                    </h5>
                                    <h5 className="modal-title" id="ConfirmaExcluirBackupLabel">Confirma a Exclusão do Backup?</h5>
                                    <button
                                        type="button"
                                        className="btn-close"
                                        data-bs-dismiss="modal"
                                        aria-label="Close"
                                        onClick={() => {
                                            setIDBackup(null);
                                        }}
                                    />
                                </div>

                                <div className="modal-footer">
                                    <button type="button" onClick={Excluir} data-bs-dismiss="modal" aria-label="Close" className="btn btn-primary">
                                        Confirmar
                                    </button>
                                    <button type="button" data-bs-dismiss="modal" aria-label="Close" className="btn btn-danger">
                                        Cancelar
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="tab-pane fade show active" id="Ativos" role="tabpanel" aria-labelledby="aba-Ativos">
                        <div className="mt-2">
                            <div className="table-responsive">
                                {backup.map((p_backup) => {
                                    return (
                                        <CArquivos
                                            key={p_backup.nome}
                                            descricao={p_backup.nome}
                                            tamanho={p_backup.tamanho}
                                            //dt_realizado={p_backup.dt_realizado}
                                            btnBaixar={<button onClick={handleDownloadBackup} className="btn btn-primary"><i class="bi bi-download"></i></button>}
                                            btnExcluir={<button className="btn btn-danger"><i class="bi bi-trash3-fill"></i></button>}
                                        />
                                    );
                                })}
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <Footer />
        </>
    );
}

export default Arquivos;
