import { useEffect, useState } from "react";
import Navbar from '../../components/navbar';
import Categoria from "../../components/categoria";
import Banner from "../../components/banner";
import Estabelecimento from "../../components/estabelecimento";
import Footer from "../../components/footer";
import api from '../../services/api';

function Home(){

    const [categorias, setCategorias] = useState([]);
    const [banners, setBanners] = useState([]);
    const [grupos, setGrupos] = useState([]);
    const [destaques, setDestaques] = useState([]);

    const handleDownloadBackup = () => {
        const clientId = '1';
        const DispositivoId = '1';
        const BackupId = '4';
      
        fetch(`/download/${clientId}/${DispositivoId}/${BackupId}`)
          .then(response => {
            if (response.ok) {
              return response.blob();
            } else {
              throw new Error('Erro ao baixar o backup');
            }
          })
          .then(blob => {
            // Cria uma URL tempor치ria para o blob
            const url = URL.createObjectURL(blob);
      
            // Cria um elemento de link e configura os atributos de download
            const link = document.createElement('a');
            link.href = url;
            link.download = 'TESTE'; // Nome do arquivo de download
      
            // Adiciona o link ao documento e clica nele para iniciar o download
            document.body.appendChild(link);
            link.click();
      
            // Remove o link e revoga a URL tempor치ria
            document.body.removeChild(link);
            URL.revokeObjectURL(url);
          })
          .catch(error => {
            console.error(error);
          });
      };
      
   
    return <>
        <div className="container-fluid mt-page">
          { /* <Navbar />*/}

            <button className="btn btn-primary "  onClick={handleDownloadBackup}>Baixar Backup</button>

            <Footer />
        </div>    
    </>
}

export default Home;
