import { useEffect, useState } from "react";
import Navbar from '../../components/navbar';
import Categoria from "../../components/categoria";
import Banner from "../../components/banner";
import Estabelecimento from "../../components/estabelecimento";
import Footer from "../../components/footer";
import api from '../../services/api';

function Home(){

    const [categorias, setCategorias] = useState([]);
    const [banners, setBanners] = useState([]);
    const [grupos, setGrupos] = useState([]);
    const [destaques, setDestaques] = useState([]);
    
    const handleDownloadBackup = () => {
      const downloadUrl = 'http://localhost:4632/download/exe';
    
      // Defina os dados a serem enviados no corpo da solicitação
      const data = {
        clientId: 'seuClientIdAqui'
      };
    
      fetch(downloadUrl, {
        method: 'POST',
        headers: {'cliente':'1',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      })
        .then(response => response.blob())
        .then(blob => {
          // Cria um link temporário para realizar o download
          const link = document.createElement('a');
          link.href = URL.createObjectURL(blob);
          link.setAttribute('download', 'BackupPlus.exe'); // Defina o nome do arquivo aqui
          link.style.display = 'none';
          document.body.appendChild(link);
          link.click();
          document.body.removeChild(link);
        })
        .catch(error => {
          console.error('Erro ao realizar o download:', error);
        });
    };
    
    

    return (
      <>
        <div className="container-fluid mt-page">
           <Navbar />
           
            <h4 className=" mt-5">Essa pagina ainda está em contrução, vá para clientes ou realize o Download da ferramenta no link.</h4>
            
          

          <Footer />
        </div>    
      </>
    );
}

export default Home;
