import Navbar from "../../components/navbar/index.jsx";
import Footer from "../../components/footer/index.jsx";
import { Link, useNavigate, useParams } from "react-router-dom";
import { IMaskInput } from 'react-imask'; 
import {CDispositivos} from "../../components/dispositivos/dispositivos.jsx";

import { useState } from "react";
import { useEffect } from "react";
import api from "../../services/api.js";


function Dispositivos(){
    
    //const {cod_comandantes, setCodComandantes} = useParams();
    const navigate = useNavigate();

    const [IdCliente, setIDCliente] = useState(localStorage.getItem("sessionCod_Cliente"));
    const [IdDispositivo, setIDDispositivo] = useState("");
    const [descricao, setDescricao] = useState("");
    const [gb, setGB] = useState("");
    const [ativo, setAtivo] = useState("");
    
    const [msg, setMsg] = useState("");

    const [dispositivos, setDispositivos] = useState([]);  
    
    function handleChange(e) {
        setAtivo(e.target.checked ? 'S' : 'N');
      }

    function Limpar(){;
        setIDDispositivo("");
        setDescricao("");
        setGB("");
    }

    function consultaDispositivos(){
        api.get('/dispositivos/S',{ headers: { 'cliente': localStorage.getItem("sessionCod_Cliente") } })
        .then((retorno) => {
            setDispositivos(retorno.data)
            console.log(retorno.data);

        })
        .catch((err) => {
            setDispositivos([]);

            if (err.response) {
                console.log(err.response.data);
            }
        });   
    } 
    
    function ConsultaDispositivoID(){
        api.get('/clientes/'+IdCliente+'/dispositivos/'+IdDispositivo)
        .then((retorno) => {
            setDispositivos(retorno.data)
            console.log(retorno.data);

        })
        .catch((err) => {
            setDispositivos([]);

            if (err.response) {
                console.log(err.response.data);
            }
        });   
    } 

    /*function ativarCliente(){
        api.put(`/clientes/ativar/`+IdCliente)
        .then((retorno) => {
            consultaClientesInativos()
            console.log(IdCliente);

        })
        .catch((err) => {
            setClientes([]);

            if (err.response) {
                console.log(err.response.data);
                
            }
        });   
    }*/  

    function Excluir(){
        api.delete('/clientes/'+IdCliente+'/dispositivos/'+IdDispositivo)
        .then((retorno) => {
            consultaDispositivos()
            console.log(IdDispositivo);
        })
        .catch((err) => {
            setDispositivos([]);
            if (err.response) {
                console.log(err.response.data);
            }
        });   
    }  

    /*function consultaClientesInativos(){
        api.get('/clientes/inativos')
        .then((retorno) => {
            setClientes(retorno.data)
            console.log(retorno.data);

        })
        .catch((err) => {
            setClientes([]);

            if (err.response) {
                console.log(err.response.data);
            }
        });    
    }*/ 

    function SalvarDados() {
        const dados = {
          cod_cliente : IdCliente ,
          descricao,
          gb,
          ativo
        };
      
        if (IdDispositivo > 0) {
          // Editar cliente existente
          api
            .put('/dispositivos/editar/'+ IdDispositivo, dados)
            .then((retorno) => {
                consultaDispositivos();
              console.log("Editado com sucesso!");
              Limpar();
            })
            .catch((err) => {
              setDispositivos([]);
      
              if (err.response) {
                console.log(err.response.data);
              }
            });
        } else {
          // Cadastrar novo Dispositivo
          api
            .post('/dispositivos/cadastro', dados)
            .then((retorno) => {
                consultaDispositivos();
              console.log("Cadastrado com sucesso!");
              Limpar();
            })
            .catch((err) => {
              setDispositivos([]);
      
              if (err.response) {
                console.log(err.response.data);
              }
            });
        }
      }

      
    function RemoveStorage(){
        localStorage.removeItem('sessionCod_Cliente');
        }

    useEffect(() => {
       consultaDispositivos();
       RemoveStorage();
    }, []);

    return <>
    <Navbar />

    <div  /* MODAL PARA CADASTRAR/EDITAR CLIENTES */ className="modal fade" id="Cadastrar" data-bs-backdrop="static" data-bs-keyboard="false" x     aria-labelledby="CadastrarComandanteLabel" aria-hidden="true">
        <div className="modal-dialog modal-dialog-centered modal-md ">
            <div className="modal-content">
                <div className="modal-header">
                    <h5 className="modal-title" id="SelecionarLabe2">Clientes</h5>
                    <button type="button" onClick={Limpar} className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>

                <div className="modal-body">
                    <p></p>       
                    <div>
                        <div className="mb-3">
                            <label>Descrição</label>
                            <input type="text" onChange={(e) => setDescricao(e.target.value)} value={descricao} className="form-control" id="modelNome" placeholder=""></input>
                        </div>                                                                              
                    </div> 
                    <div>
                        <div className="mb-3">
                            <label>GB</label>
                            <input type="number" onChange={(e) => setGB(e.target.value)} value={gb} className="form-control" id="modelNome" placeholder=""></input>
                        </div>                                                                              
                    </div> 
                    <div className="form-group mt-3 col-sm-12">
                            <input  type="checkbox" checked={ativo === 'S'}
                                    onChange={handleChange} value={ativo}
                                    className="form-check-input"
                                    id="CheckAtivo"
                                    placeholder=""
                            />
                            <label className="ms-1 form-check-label" htmlFor="CheckAtivo">
                                Ativo
                            </label>
                        </div>
                                                               
                </div>    

                <div className="modal-footer">
                    <button type="button" onClick={SalvarDados} data-bs-dismiss="modal" aria-label="Close" className="btn btn-primary">Salvar</button>
                </div>
            </div>
        </div>
    </div>
    <div  className="mt-5 container-fluid p-5">

        <h2>Dispositivos</h2>
        
        <div /*ABAS*/ className="tab-content" id="conteudoMinhaAba">

            <div  /* MODAL PARA EXCLUIR */ className="modal fade" id="ConfirmaExcluirDispositivo" data-bs-backdrop="static" 
                        data-bs-keyboard="false" aria-labelledby="ConfirmaExcluirClienteLabel" aria-hidden="true">
                    <div className="modal-dialog modal-dialog-centered modal-md ">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="ConfirmaExcluirDispositivoLabel">Confirma a Exclusão do Dispositivo?</h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    data-bs-dismiss="modal"
                                    aria-label="Close"
                                    onClick={() => {
                                        setIDDispositivo
                                        (null);
                                    }}>
                                </button>
                            </div>
                            
                            <div className="modal-footer">
                                <button type="button" onClick={Excluir} data-bs-dismiss="modal" aria-label="Close" className="btn btn-primary">Confirmar</button>
                                <button type="button" data-bs-dismiss="modal" aria-label="Close" className="btn btn-danger">Cancelar</button>
                            </div>  
                        </div>
                    </div>
                </div>
            <div /*ATIVOS*/ className="tab-pane fade show active" id="Ativos" role="tabpanel" aria-labelledby="aba-Ativos">
                <div className="mt-2">  
                    <button /*BOTÃO CHAMADA DO MODAL DE CADASTRAR*/ type="button" className="btn mb-2 btn-success" onClick={() => {
                                            setIDCliente("");
                                            setIDDispositivo(""); setDescricao("");setGB("")
                                          }} data-bs-toggle="modal" data-bs-target="#Cadastrar">
                        Cadastrar Dispositivo
                    </button>
                    <div /*TABELA DE VIEW*/ className="table-responsive"> 

                        <table className="table table-hover table-striped">
                            <caption>Lista de Dispositivos Ativos</caption>
                            <thead>
                                <tr className="align-middle">
                                    <th scope="col">Codigo</th>
                                    <th scope="col">Descrição</th>
                                    <th scope="col">Usado</th>
                                    <th scope="col">Disponivel</th>
                                    <th scope="col">Data de Cadastro</th>
                                    <th className="text-center" scope="col">Ações</th>
                                </tr>
                            </thead>
                            <tbody>
                            {
                                    dispositivos.map((p_dispositivos) => {
                                    return <CDispositivos key={p_dispositivos.cod_dispositivo}
                                        codigo={p_dispositivos.cod_dispositivo}
                                        descricao={p_dispositivos.descricao}
                                        usado={p_dispositivos.gb}
                                        disponivel={p_dispositivos.gb}
                                        dt_cadastro={p_dispositivos.dt_cadastro}

                                        configurar={<a
                                            type="button" href="/backup" 
                                            onClick={() => {
                                            localStorage.setItem('sessionCod_Dispositivo', p_dispositivos.cod_dispositivo);
                                            }} 
                                            className="btn btn-success m-0 mt-0"
                                        ><i class="bi bi-gear-wide-connected"></i>
                                        </a>}
                                        
                                        editar={<button type="button"  onClick={() => {
                                            setIDCliente(p_dispositivos.cod_cliente);
                                            setIDDispositivo(p_dispositivos.cod_dispositivo);
                                            setDescricao(p_dispositivos.descricao); setGB(p_dispositivos.gb); setAtivo(p_dispositivos.ativo)
                                          }} data-bs-toggle="modal" data-bs-target="#Cadastrar"  
                                                 className="btn btn-primary m-1 mt-1"><i className="bi bi-pencil-fill"></i></button>}
                                        
                                        
                                        
                                        excluir={<button
                                            type="button"
                                            onClick={() => {
                                              setIDDispositivo(p_dispositivos.cod_dispositivo);
                                              setIDCliente(p_dispositivos.cod_cliente);
                                            }}
                                            data-bs-toggle="modal" 
                                            data-bs-target="#ConfirmaExcluirCliente" 
                                            className="btn btn-danger m-0 mt-0"
                                          >
                                           <i className="bi bi-trash3-fill"></i>
                                          </button>}
                                    />
                                    })
                                }       
                            </tbody>
                        </table> 
                    </div>
                </div>
            </div>
        </div>
    </div>

    <Footer />
</>
}

export default Dispositivos;
