function CClientes(props){

    const dt_cadastro = new Date(props.dt_cadastro);
    const tamanhoFixo = (props.usado / 1024).toFixed(2)


    return <tr className="align-middle">
        <td>{props.codigo}</td>
        <td>{props.nome}</td>
        <td> {tamanhoFixo > 999999.99
                    ? `${(tamanhoFixo / 1024 / 1024).toFixed(2)} GB`
                    : `${(tamanhoFixo / 1024).toFixed(2)} MB`}
                    </td>
        <td>{props.disponivel}</td>
        <td>{new Intl.DateTimeFormat('pt-BR', {dateStyle: 'short',timeStyle: 'short'}).format(dt_cadastro)}</td>
        <td className="text-center">
            {props.backup}
            {props.editar}
            {props.excluir}
        </td>
    </tr>
}

function CClientes_inativos(props){

    const dt_cadastro = new Date(props.dt_cadastro);
    const dt_exclusao = new Date(props.dt_exclusao);

    return <tr className="align-middle">
       <td>{props.codigo}</td>
        <td>{props.nome}</td>
        <td className="text-center">{new Intl.DateTimeFormat('pt-BR', {dateStyle: 'short',timeStyle: 'short'}).format(dt_cadastro)}</td>
        <td className="text-center"> {props.ativar} </td>
    </tr>
}


export {CClientes, CClientes_inativos };
